import { useState } from "react";
import { useEffect } from 'react/cjs/react.development';

function App() {  
  const [loading, setLoading] = useState(true);  
  const [coins, setCoins] = useState([]);
  // const [dollar, setDollars] = useState(0);

  useEffect(()=>{
    fetch("https://api.coinpaprika.com/v1/tickers/btc-bitcoin")    
    .then((response) => response.json())
    .then((json) => { setCoins(json); setLoading(false); } );
  }, [])
  return (
    <div>
      <h1>코인 ({coins.length})</h1>
        { loading? <strong>로딩중...</strong> : null }        
        {/* {coins.map((coin, index) => { return <option key={index}>{coin.name}({coin.symbol}: ${coin.quotes.USD.price} USD</option> } )}         */}
        {coins.symbol}: ${coins.quotes.USD.price} USD
        <input type="text" placeholder="USD를 입력하세요" />
        <button>변환</button>
    </div>
  );
}

export default App;
