import { useState, useEffect } from "react";

function App() {
  const [counter1, setValue] = useState(0);  
  const onClick = () => setValue( (prev) => prev + 1);
  console.log("call an api");
  const iOnlyOnce = () => {
    console.log("i run only once");
  }
  useEffect(iOnlyOnce, []);

  return (
    <div>
      <h1>{counter1}</h1>
      <button onClick={onClick}>click me</button>
    </div>
  );
}

export default App;
